import enum


class Direction(enum.Enum):
    UP = (lambda x, y: (x, y - 1), '↑')
    DOWN = (lambda x, y: (x, y + 1), '↓')
    LEFT = (lambda x, y: (x - 1, y), '←')
    RIGHT = (lambda x, y: (x + 1, y), '→')

    def __repr__(self):
        return self.value[1]

    def __call__(self, *args, **kwargs):
        return self.value[0](*args, **kwargs)

    def __str__(self):
        return self.__repr__()


_TURNS = {
    Direction.UP: (Direction.LEFT, Direction.RIGHT),
    Direction.DOWN: (Direction.LEFT, Direction.RIGHT),
    Direction.LEFT: (Direction.UP, Direction.DOWN),
    Direction.RIGHT: (Direction.UP, Direction.DOWN),
}


def part1(data):
    maze = [list(x) for x in data.splitlines()]
    pos = (maze[0].index('|'), 0)
    d = Direction.DOWN

    def _get(x, y):
        if x < 0 or y < 0 or y > len(maze) or x > len(maze[y]):
            return None
        return maze[y][x]

    def _set(x, y, v):
        maze[y][x] = v

    print(*(''.join(x) for x in maze), sep='\n')

    letters = ''
    steps = 0
    while True:
        pos = d(*pos)
        tile = _get(*pos)
        print(pos, d, tile)

        steps += 1

        if tile == '|' or tile == '-' or tile == 'X':
            continue
        elif tile == '+':
            for x in _TURNS[d]:
                t = _get(*x(*pos))
                if t and t != ' ':
                    d = x
                    break
        elif tile == ' ':
            break
        else:
            letters += tile

    return letters, steps


